import BN from 'bn.js';
import { assert } from "../assert.mjs"; // https://golb.hplar.ch/2018/09/javascript-bigint.html

function newtonIteration(n, x0) {
  const x1 = n.div(x0).add(x0).shrn(1);

  if (x0.eq(x1) || x0.eq(x1.subn(1))) {
    return x0;
  }

  return newtonIteration(n, x1);
}
/**
 * @name bnSqrt
 * @summary Calculates the integer square root of a BN
 * @example
 * <BR>
 *
 * ```javascript
 * import BN from 'bn.js';
 * import { bnSqrt } from '@polkadot/util';
 *
 * bnSqrt(new BN(16)).toString(); // => '4'
 * ```
 */


export function bnSqrt(value) {
  assert(value.gten(0), 'square root of negative numbers is not supported');
  return value.ltn(2) ? value : newtonIteration(value, new BN(1));
}